<?php

namespace AppBundle\Repository;
use Doctrine\ORM\Tools\Pagination\Paginator;

/**
 * PersonaRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class PersonaRepository extends \Doctrine\ORM\EntityRepository
{
    
     public function findLikeName($term) {
       $qb = $this->getEntityManager()->createQueryBuilder();
        $result = $qb->select('n')
                ->from('AppBundle:Persona', 'n')
                ->where($qb->expr()
                        ->like('n.nombre', $qb->expr()
                                ->literal('%' . $term . '%')))
                ->setMaxResults(10)->getQuery()->getResult();
        return $result;
    }
    
      public function getAllPers($currentPage = 1, $limit = 15)
    {
          
        $em = $this->getEntityManager();
        
        $dql= "SELECT e FROM AppBundle\Entity\Persona e ORDER BY e.id";

        $query = $em->createQuery($dql)
                ->setFirstResult($limit*($currentPage-1))
                ->setMaxResults($limit);


        $paginator = new Paginator($query, $fetchJoinCollection=true);

        return $paginator;
    }

    
}
